mapper.select:
select
	t1.id                             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.is_contract_related            ,
	t1.apply_type                     ,
	t1.apply_id                       ,
	t1.apply_device_name              ,
	t1.apply_num                      ,
	t1.apply_price                    ,
	t1.apply_spec                     ,
	t1.apply_model                    ,
	t1.apply_manufacturer             ,
	t1.contract_id                    ,
	t1.contract_num                   ,
	t1.contract_name                  ,
	t1.contract_total_price           ,
	t1.contract_supplier              ,
	t1.contract_operator              ,
	t1.contract_device_name           ,
	t1.contract_device_num            ,
	t1.contract_device_price          ,
	t1.contract_device_total_price    ,
	t1.contract_spec                  ,
	t1.contract_model                 ,
	t1.contract_manufacturer          ,
	t1.contract_state                 ,
	t1.GUARANTEE_PERIOD				  ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   ,
	t1.is_derive                      
from assets_apply_module 	t1


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.IS_CONTRACT_RELATED =#{isContractRelated}


			and t1.APPLY_TYPE =#{applyType}


			and t1.APPLY_ID =#{applyId}


			and t1.APPLY_DEVICE_NAME =#{applyDeviceName}


			and t1.APPLY_NUM =#{applyNum}


			and t1.APPLY_PRICE =#{applyPrice}


			and t1.APPLY_SPEC =#{applySpec}


			and t1.APPLY_MODEL =#{applyModel}


			and t1.APPLY_MANUFACTURER =#{applyManufacturer}


			and t1.CONTRACT_ID =#{contractId}


			and t1.CONTRACT_NUM =#{contractNum}


			and t1.CONTRACT_NAME =#{contractName}


			and t1.CONTRACT_TOTAL_PRICE =#{contractTotalPrice}


			and t1.CONTRACT_SUPPLIER =#{contractSupplier}


			and t1.CONTRACT_OPERATOR =#{contractOperator}


			and t1.CONTRACT_DEVICE_NAME =#{contractDeviceName}


			and t1.CONTRACT_DEVICE_NUM =#{contractDeviceNum}


			and t1.CONTRACT_DEVICE_PRICE =#{contractDevicePrice}


			and t1.CONTRACT_DEVICE_TOTAL_PRICE =#{contractDeviceTotalPrice}


			and t1.CONTRACT_SPEC =#{contractSpec}


			and t1.CONTRACT_MODEL =#{contractModel}


			and t1.CONTRACT_MANUFACTURER =#{contractManufacturer}


			and t1.CONTRACT_STATE =#{contractState}


			and t1.GUARANTEE_PERIOD =#{guaranteePeriod}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}


			and t1.IS_DERIVE =#{isDerive}
