	t1.attribute_24                   ,
	t1.attribute_25                   ,
	t1.attribute_26                   ,
	t1.attribute_27                   ,
	t1.attribute_28                   ,
	t1.attribute_29                   ,
	t1.attribute_30                   ,
	t1.unified_id                     ,
	t1.device_name                    ,
	t1.device_category                ,
	t1.device_model                   ,
	t1.product_area                   ,
	t1.position_id                    ,
	t1.product_date                   ,
	t1.user_id                        ,
	t1.user_dept                      ,
	t1.last_acc_check_date            ,
	t1.acc_check_cycle                ,
