			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}


			and t1.ATTRIBUTE_21 =#{attribute21}


			and t1.ATTRIBUTE_22 =#{attribute22}


			and t1.ATTRIBUTE_23 =#{attribute23}


			and t1.ATTRIBUTE_24 =#{attribute24}


			and t1.ATTRIBUTE_25 =#{attribute25}


			and t1.ATTRIBUTE_26 =#{attribute26}


			and t1.ATTRIBUTE_27 =#{attribute27}


			and t1.ATTRIBUTE_28 =#{attribute28}


			and t1.ATTRIBUTE_29 =#{attribute29}


			and t1.ATTRIBUTE_30 =#{attribute30}


			and t1.UNIFIED_ID =#{unifiedId}


			and t1.DEVICE_NAME =#{deviceName}


			and t1.DEVICE_CATEGORY =#{deviceCategory}


			and t1.DEVICE_MODEL =#{deviceModel}


			and t1.PRODUCT_AREA =#{productArea}


			and t1.POSITION_ID =#{positionId}


			and t1.PRODUCT_DATE >= #{productDateBegin}


			and t1.PRODUCT_DATE <= #{productDateEnd}


			and t1.USER_ID =#{userId}


			and t1.USER_DEPT =#{userDept}


			and t1.LAST_ACC_CHECK_DATE >= #{lastAccCheckDateBegin}


			and t1.LAST_ACC_CHECK_DATE <= #{lastAccCheckDateEnd}


			and t1.ACC_CHECK_CYCLE =#{accCheckCycle}


			and t1.NEXT_ACC_CHECK_DATE >= #{nextAccCheckDateBegin}


			and t1.NEXT_ACC_CHECK_DATE <= #{nextAccCheckDateEnd}


			and t1.ATTACHMENT =#{attachment}


			and t1.PROC_ID =#{procId}


			and t1.CHECK_ID =#{checkId}


			and t1.ACC_CHECK_DATE >= #{accCheckDateBegin}


			and t1.ACC_CHECK_DATE <= #{accCheckDateEnd}

SELECT s.CHECK_ID,max(s.ACC_CHECK_DATE) ACC_CHECK_DATE,s.ACC_CHECK_CYCLE  FROM "ASSETS_DEVICE_ACHECK_PLAN" s
where s.PROC_ID= #{procId}
group by s.CHECK_ID,s.ACC_CHECK_CYCLE
