select
v.activityalias_                 ,
v.businessstate_                 ,
t1.id                             ,
t1.created_by                     ,
t1.creation_date                  ,
t1.last_updated_by                ,
t1.last_update_date               ,
t1.last_update_ip                 ,
t1.version                        ,
t1.attribute_01                   ,
t1.attribute_02                   ,
t1.attribute_03                   ,
t1.attribute_04                   ,
t1.attribute_05                   ,
t1.attribute_06                   ,
t1.attribute_07                   ,
t1.attribute_08                   ,
t1.attribute_09                   ,
t1.attribute_10                   ,
t1.attribute_11                   ,
t1.attribute_12                   ,
t1.attribute_13                   ,
t1.attribute_14                   ,
t1.attribute_15                   ,
t1.attribute_16                   ,
t1.attribute_17                   ,
t1.attribute_18                   ,
t1.attribute_19                   ,
t1.attribute_20                   ,
t1.attribute_21                   ,
t1.attribute_22                   ,
t1.attribute_23                   ,
t1.attribute_24                   ,
t1.attribute_25                   ,
t1.attribute_26                   ,
t1.attribute_27                   ,
t1.attribute_28                   ,
t1.attribute_29                   ,
t1.attribute_30                   ,
t1.checks_no                      ,
t1.plan_name                      ,
t1.generate_by                    ,
t1.generate_date                  ,
t1.implementation_department      ,
t1.checks_number                  ,
t1.telephone                      ,
t1.next_regular_start_date        ,
t1.next_regular_end_date          ,
t1.attachment
from assets_device_rcheck_proc 	t1 left join bpm_client_hist_procinst_v v on t1.id = v.formid_


		and t1.ID =#{id}


		and t1.CREATED_BY =#{createdBy}


		and t1.CREATION_DATE =#{creationDate}


		and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


		and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


		and t1.LAST_UPDATE_IP =#{lastUpdateIp}


		and t1.VERSION =#{version}


		and t1.ATTRIBUTE_01 =#{attribute01}


		and t1.ATTRIBUTE_02 =#{attribute02}


		and t1.ATTRIBUTE_03 =#{attribute03}


		and t1.ATTRIBUTE_04 =#{attribute04}


		and t1.ATTRIBUTE_05 =#{attribute05}


		and t1.ATTRIBUTE_06 =#{attribute06}


		and t1.ATTRIBUTE_07 =#{attribute07}


		and t1.ATTRIBUTE_08 =#{attribute08}


		and t1.ATTRIBUTE_09 =#{attribute09}


		and t1.ATTRIBUTE_10 =#{attribute10}


		and t1.ATTRIBUTE_11 =#{attribute11}


		and t1.ATTRIBUTE_12 =#{attribute12}


		and t1.ATTRIBUTE_13 =#{attribute13}


		and t1.ATTRIBUTE_14 =#{attribute14}


		and t1.ATTRIBUTE_15 =#{attribute15}


		and t1.ATTRIBUTE_16 =#{attribute16}


		and t1.ATTRIBUTE_17 =#{attribute17}


		and t1.ATTRIBUTE_18 =#{attribute18}


		and t1.ATTRIBUTE_19 =#{attribute19}


		and t1.ATTRIBUTE_20 =#{attribute20}


		and t1.ATTRIBUTE_21 =#{attribute21}


		and t1.ATTRIBUTE_22 =#{attribute22}


		and t1.ATTRIBUTE_23 =#{attribute23}


		and t1.ATTRIBUTE_24 =#{attribute24}


		and t1.ATTRIBUTE_25 =#{attribute25}


		and t1.ATTRIBUTE_26 =#{attribute26}


		and t1.ATTRIBUTE_27 =#{attribute27}


		and t1.ATTRIBUTE_28 =#{attribute28}


		and t1.ATTRIBUTE_29 =#{attribute29}


		and t1.ATTRIBUTE_30 =#{attribute30}


		and t1.CHECKS_NO =#{checksNo}


		and t1.PLAN_NAME =#{planName}


		and t1.GENERATE_BY =#{generateBy}


		and t1.GENERATE_DATE >= #{generateDateBegin}


		and t1.GENERATE_DATE <= #{generateDateEnd}


		and t1.IMPLEMENTATION_DEPARTMENT =#{implementationDepartment}


		and t1.CHECKS_NUMBER =#{checksNumber}


		and t1.TELEPHONE =#{telephone}


		and t1.NEXT_REGULAR_START_DATE >= #{nextRegularStartDateBegin}


		and t1.NEXT_REGULAR_START_DATE <= #{nextRegularStartDateEnd}


		and t1.NEXT_REGULAR_END_DATE >= #{nextRegularEndDateBegin}


		and t1.NEXT_REGULAR_END_DATE <= #{nextRegularEndDateEnd}


		and t1.ATTACHMENT =#{attachment}


		and exists (select 1 from BPM_CLIENT_HIST_TASK_V os where os.task_b_id_ = t1.id and os.assignee_ = #{currUserId})


		and v.businessstate_ = #{bpmState}
