mapper.select:
select
	v.activityalias_                 ,
	v.businessstate_                 ,
	t1.id                             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.proc_id						  ,
	t1.applicant_id                   ,
	t1.applicant_depart               ,
	t1.unified_id                     ,
	t1.form_state                     ,
	t1.device_name                    ,
	t1.device_category                ,
	t1.device_spec                    ,
	t1.device_model                   ,
	t1.owner_id                       ,
	t1.owner_dept                     ,
	t1.contact                        ,
	t1.position                       ,
	t1.manufacturer                   ,
	t1.is_pc                          ,
	t1.is_test_device                 ,
	t1.is_metering                    ,
	t1.repair_dept                    ,
	t1.failure_desc                   ,
	t1.repair_desc                    ,
	t1.repair_finish_time             ,
	t1.finish_ack                     ,
	t1.repair_quality                 ,
	t1.failure_analysis_id            ,
	t1.failure_analysis_desc          ,
	t1.service_attitude               ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   ,
	t1.repair_progress                ,
	t1.repair_plan_staff              ,
	t1.repair_staff                   ,
	t1.meter_plan_staff               ,
	t1.has_extra_expense              ,
	t1.extra_expense_explain          
from assets_device_repair 	t1 left join bpm_client_hist_procinst_v v on t1.id = v.formid_ 


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.PROC_ID =#{procId}


			and t1.APPLICANT_ID =#{applicantId}


			and t1.APPLICANT_DEPART =#{applicantDepart}


			and t1.UNIFIED_ID =#{unifiedId}


			and t1.FORM_STATE =#{formState}


			and t1.DEVICE_NAME =#{deviceName}


			and t1.DEVICE_CATEGORY =#{deviceCategory}


			and t1.DEVICE_SPEC =#{deviceSpec}


			and t1.DEVICE_MODEL =#{deviceModel}


			and t1.OWNER_ID =#{ownerId}


			and t1.OWNER_DEPT =#{ownerDept}


			and t1.CONTACT =#{contact}


			and t1.POSITION =#{position}


			and t1.MANUFACTURER =#{manufacturer}


			and t1.IS_PC =#{isPc}


			and t1.IS_TEST_DEVICE =#{isTestDevice}


			and t1.IS_METERING =#{isMetering}


			and t1.REPAIR_DEPT =#{repairDept}


			and t1.FAILURE_DESC =#{failureDesc}


			and t1.REPAIR_DESC =#{repairDesc}


			and t1.REPAIR_FINISH_TIME >= #{repairFinishTimeBegin}


			and t1.REPAIR_FINISH_TIME <= #{repairFinishTimeEnd}


			and t1.FINISH_ACK =#{finishAck}


			and t1.REPAIR_QUALITY =#{repairQuality}


			and t1.FAILURE_ANALYSIS_ID =#{failureAnalysisId}


			and t1.FAILURE_ANALYSIS_DESC =#{failureAnalysisDesc}


			and t1.SERVICE_ATTITUDE =#{serviceAttitude}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}


			and t1.REPAIR_PROGRESS =#{repairProgress}


			and t1.REPAIR_PLAN_STAFF =#{repairPlanStaff}


			and t1.REPAIR_STAFF =#{repairStaff}


			and t1.METER_PLAN_STAFF =#{meterPlanStaff}


			and t1.HAS_EXTRA_EXPENSE =#{hasExtraExpense}


			and t1.EXTRA_EXPENSE_EXPLAIN =#{extraExpenseExplain}


			 and exists (select 1 from BPM_CLIENT_HIST_TASK_V os where os.task_b_id_ = t1.id and os.assignee_ = #{currUserId}) 


			and v.businessstate_ = #{bpmState}
