mapper.select:
select
	v.activityalias_                 ,
	v.businessstate_                 ,
	t1.id                             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.created_by_person              ,
	t1.created_by_dept                ,
	t1.created_by_tel                 ,
	t1.form_state                     ,
	t1.proc_name                      ,
	t1.proc_id                        ,
	t1.asset_id                       ,
	t1.unified_id                     ,
	t1.device_category                ,
	t1.device_name                    ,
	t1.device_model                   ,
	t1.device_spec                    ,
	t1.owner_id                       ,
	t1.owner_dept                     ,
	t1.user_id                        ,
	t1.user_dept                      ,
	t1.manufacturer_id                ,
	t1.created_date                   ,
	t1.position_id                    ,
	t1.original_value                 ,
	t1.total_depreciation             ,
	t1.net_value                      ,
	t1.net_salvage                    ,
	t1.secret_level                   ,
	t1.scrap_reason                   ,
	t1.product_risk_analyse           ,
	t1.scrap_money                    ,
	t1.recovery_store                 ,
	t1.device_id                      
from assets_device_scrap 	t1 left join bpm_client_hist_procinst_v v on t1.id = v.formid_ 


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.CREATED_BY_PERSON =#{createdByPerson}


			and t1.CREATED_BY_DEPT =#{createdByDept}


			and t1.CREATED_BY_TEL =#{createdByTel}


			and t1.FORM_STATE =#{formState}


			and t1.PROC_NAME =#{procName}


			and t1.PROC_ID =#{procId}


			and t1.ASSET_ID =#{assetId}


			and t1.UNIFIED_ID =#{unifiedId}


			and t1.DEVICE_CATEGORY =#{deviceCategory}


			and t1.DEVICE_NAME =#{deviceName}


			and t1.DEVICE_MODEL =#{deviceModel}


			and t1.DEVICE_SPEC =#{deviceSpec}


			and t1.OWNER_ID =#{ownerId}


			and t1.OWNER_DEPT =#{ownerDept}


			and t1.USER_ID =#{userId}


			and t1.USER_DEPT =#{userDept}


			and t1.MANUFACTURER_ID =#{manufacturerId}


			and t1.CREATED_DATE >= #{createdDateBegin}


			and t1.CREATED_DATE <= #{createdDateEnd}


			and t1.POSITION_ID =#{positionId}


			and t1.ORIGINAL_VALUE =#{originalValue}


			and t1.TOTAL_DEPRECIATION =#{totalDepreciation}


			and t1.NET_VALUE =#{netValue}


			and t1.NET_SALVAGE =#{netSalvage}


			and t1.SECRET_LEVEL =#{secretLevel}


			and t1.SCRAP_REASON =#{scrapReason}


			and t1.PRODUCT_RISK_ANALYSE =#{productRiskAnalyse}


			and t1.SCRAP_MONEY =#{scrapMoney}


			and t1.RECOVERY_STORE =#{recoveryStore}


			and t1.DEVICE_ID =#{deviceId}


			 and exists (select 1 from BPM_CLIENT_HIST_TASK_V os where os.task_b_id_ = t1.id and os.assignee_ = #{currUserId}) 


			and v.businessstate_ = #{bpmState}
