	t1.attribute_30                   
from assets_operation_certificate 	t1


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.CERTIFICATE_NAME =#{certificateName}


			and t1.CERTIFICATE_NUM =#{certificateNum}


			and t1.CERTIFICATE_TYPE =#{certificateType}


			and t1.CERTIFICATE_STATUS =#{certificateStatus}


			and t1.CERTIFICATE_DEVICE_COUNT =#{certificateDeviceCount}


			and t1.CERTIFICATE_INFO =#{certificateInfo}


			and t1.HOLDER_ID =#{holderId}


			and t1.HOLDER_DEPT =#{holderDept}


			and t1.HOLDER_PHONE =#{holderPhone}


			and t1.MANAGER_DEPT =#{managerDept}


			and t1.MANAGER_ID =#{managerId}


			and t1.CREATED_DATE >= #{createdDateBegin}


			and t1.CREATED_DATE <= #{createdDateEnd}


			and t1.SERIAL_NUMBER =#{serialNumber}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


