mapper.select:
select
	t1.id                             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.unified_id                     ,
	t1.device_name                    ,
	t1.component_model                ,
	t1.component_num                  ,
	t1.component_name                 ,
	t1.component_manufacturer         ,
	t1.component_install_position     ,
	t1.component_driver_id            ,
	t1.component_driver_version       ,
	t1.component_logic_id             ,
	t1.component_logic_version        ,
	t1.component_document             ,
	t1.remarks                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   
from assets_tdevice_component 	t1


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.UNIFIED_ID =#{unifiedId}


			and t1.DEVICE_NAME =#{deviceName}


			and t1.COMPONENT_MODEL =#{componentModel}


			and t1.COMPONENT_NUM =#{componentNum}


			and t1.COMPONENT_NAME =#{componentName}


			and t1.COMPONENT_MANUFACTURER =#{componentManufacturer}


			and t1.COMPONENT_INSTALL_POSITION =#{componentInstallPosition}


			and t1.COMPONENT_DRIVER_ID =#{componentDriverId}


			and t1.COMPONENT_DRIVER_VERSION =#{componentDriverVersion}


			and t1.COMPONENT_LOGIC_ID =#{componentLogicId}


			and t1.COMPONENT_LOGIC_VERSION =#{componentLogicVersion}


			and t1.COMPONENT_DOCUMENT =#{componentDocument}


			and t1.REMARKS =#{remarks}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}
