mapper.select:
select
	t1.id                             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.unified_id                     ,
	t1.device_name                    ,
	t1.software_name                  ,
	t1.software_basic_name            ,
	t1.software_id                    ,
	t1.software_code                  ,
	t1.software_version               ,
	t1.software_release_num           ,
	t1.software_period                ,
	t1.software_code_size             ,
	t1.software_leader                ,
	t1.software_language              ,
	t1.software_run_environment       ,
	t1.software_dev_environment       ,
	t1.remarks                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   
from assets_tdevice_software 	t1


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.UNIFIED_ID =#{unifiedId}


			and t1.DEVICE_NAME =#{deviceName}


			and t1.SOFTWARE_NAME =#{softwareName}


			and t1.SOFTWARE_BASIC_NAME =#{softwareBasicName}


			and t1.SOFTWARE_ID =#{softwareId}


			and t1.SOFTWARE_CODE =#{softwareCode}


			and t1.SOFTWARE_VERSION =#{softwareVersion}


			and t1.SOFTWARE_RELEASE_NUM =#{softwareReleaseNum}


			and t1.SOFTWARE_PERIOD =#{softwarePeriod}


			and t1.SOFTWARE_CODE_SIZE =#{softwareCodeSize}


			and t1.SOFTWARE_LEADER =#{softwareLeader}


			and t1.SOFTWARE_LANGUAGE =#{softwareLanguage}


			and t1.SOFTWARE_RUN_ENVIRONMENT =#{softwareRunEnvironment}


			and t1.SOFTWARE_DEV_ENVIRONMENT =#{softwareDevEnvironment}


			and t1.REMARKS =#{remarks}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}
