mapper.update:
update assets_tech_transform_device t1
set  t1.device_sn = t1.device_sn + 2
where t1.project_id = #{projectId} AND t1.device_sn > #{startSn}

update assets_tech_transform_device t1
set  t1.device_sn = t1.device_sn - 1
where t1.project_id = #{projectId} AND t1.device_sn > #{startSn}
