mapper.select:
select
	v.activityalias_                 ,
	v.businessstate_                 ,
	t1.id                             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.metering_id                    ,
	t1.applicant_id                   ,
	t1.applicant_depart               ,
	t1.device_user_id                 ,
	t1.device_user_dept               ,
	t1.form_state                     ,
	t1.unified_id                     ,
	t1.device_name                    ,
	t1.device_category                ,
	t1.device_spec                    ,
	t1.device_model                   ,
	t1.manufacturer_id                ,
	t1.last_metering_date             ,
	t1.meter_person                   ,
	t1.meter_conclusion               ,
	t1.device_metrics                 ,
	t1.calibration_result             ,
	t1.effect_analyse                 ,
	t1.quality_safe_opinion           ,
	t1.chief_opinion                  ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   ,
	t1.proc_id                        ,
	t1.data_24                        ,
	t1.has_effection                  
from assets_trackback 	t1 left join bpm_client_hist_procinst_v v on t1.id = v.formid_ 


			and t1.ID =#{id}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.METERING_ID =#{meteringId}


			and t1.APPLICANT_ID =#{applicantId}


			and t1.APPLICANT_DEPART =#{applicantDepart}


			and t1.DEVICE_USER_ID =#{deviceUserId}


			and t1.DEVICE_USER_DEPT =#{deviceUserDept}


			and t1.FORM_STATE =#{formState}


			and t1.UNIFIED_ID =#{unifiedId}


			and t1.DEVICE_NAME =#{deviceName}


			and t1.DEVICE_CATEGORY =#{deviceCategory}


			and t1.DEVICE_SPEC =#{deviceSpec}


			and t1.DEVICE_MODEL =#{deviceModel}


			and t1.MANUFACTURER_ID =#{manufacturerId}


			and t1.LAST_METERING_DATE >= #{lastMeteringDateBegin}


			and t1.LAST_METERING_DATE <= #{lastMeteringDateEnd}


			and t1.METER_PERSON =#{meterPerson}


			and t1.METER_CONCLUSION =#{meterConclusion}


			and t1.DEVICE_METRICS =#{deviceMetrics}


			and t1.CALIBRATION_RESULT =#{calibrationResult}


			and t1.EFFECT_ANALYSE =#{effectAnalyse}


			and t1.QUALITY_SAFE_OPINION =#{qualitySafeOpinion}


			and t1.CHIEF_OPINION =#{chiefOpinion}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}


			and t1.PROC_ID =#{procId}


			and t1.DATA_24 =#{data24}


			and t1.HAS_EFFECTION =#{hasEffection}


			 and exists (select 1 from BPM_CLIENT_HIST_TASK_V os where os.task_b_id_ = t1.id and os.assignee_ = #{currUserId}) 


			and v.businessstate_ = #{bpmState}
