			t1.document_describe ,
			t1.person_liable ,
			t1.participants ,
			t1.document_update_by ,
			t1.document_update_date ,
			t1.belong_project ,
			t1.warehouse_catelog ,
			t1.know_scope ,
			t1.document_label ,
			t1.click_count ,
			t1.download_count ,
			t1.comment_count ,
			t1.evaluate_score ,
			t1.share_count ,
