			t1.attribute_27 ,
			t1.attribute_28 ,
			t1.attribute_29 ,
			t1.attribute_30 ,
			t1.document_url
        from document_item t1


                and t1.ID =#{id}


                and t1.PACKAGE_ID =#{packageId}


                and t1.CREATED_BY =#{createdBy}


                and t1.CREATED_BY_DEPT =#{createdByDept}


                and t1.CREATION_DATE =#{creationDate}


                and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


                and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


                and t1.LAST_UPDATE_IP =#{lastUpdateIp}


                and t1.DOCUMENT_NAME =#{documentName}


                and t1.DOCUMENT_TYPE in (#{documentType})


                and t1.DOCUMENT_CATEGORY in (#{documentCategory})


                and t1.LIFE_STAGE in (#{lifeStage})


                and t1.DOCUMENT_AUTHOR =#{documentAuthor}


                and t1.AUTHOR_DEPT =#{authorDept}


                and t1.KEY_WORD =#{keyWord}


                and t1.CURRENT_VERSION =#{currentVersion}


                and t1.LAST_VERSION =#{lastVersion}


                and t1.DOCUMENT_STATE in (#{documentState})


                and t1.LANGUAGE_CATEGORY in (#{languageCategory})


                and t1.SECRET_LEVEL in (#{secretLevel})


                and t1.DOCUMENT_ABSTRACT =#{documentAbstract}


                and t1.DOCUMENT_DESCRIBE =#{documentDescribe}


                and t1.PERSON_LIABLE =#{personLiable}


                and t1.PARTICIPANTS in (#{participants})


                and t1.DOCUMENT_UPDATE_BY =#{documentUpdateBy}


                and t1.DOCUMENT_UPDATE_DATE >= #{documentUpdateDateBegin}


                and t1.DOCUMENT_UPDATE_DATE <= #{documentUpdateDateEnd}


                and t1.BELONG_PROJECT =#{belongProject}


                and t1.WAREHOUSE_CATELOG =#{warehouseCatelog}


                and t1.KNOW_SCOPE in (#{knowScope})


                and t1.DOCUMENT_LABEL =#{documentLabel}


                and t1.CLICK_COUNT =#{clickCount}


                and t1.DOWNLOAD_COUNT =#{downloadCount}


                and t1.COMMENT_COUNT =#{commentCount}


                and t1.EVALUATE_SCORE =#{evaluateScore}


                and t1.SHARE_COUNT =#{shareCount}


                and t1.LIKED_COUNT =#{likedCount}


                and t1.ATTRIBUTE_01 =#{attribute01}


                and t1.ATTRIBUTE_02 =#{attribute02}


                and t1.ATTRIBUTE_03 =#{attribute03}


                and t1.ATTRIBUTE_04 =#{attribute04}


                and t1.ATTRIBUTE_05 =#{attribute05}


