mapper.select:
select
	t1.id                             ,
	t1.reconstruction_id_r            ,
	t1.created_by                     ,
	t1.created_by_dept_r              ,
	t1.form_state_r                   ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.owner_dept_r                   ,
	t1.owner_id_r                     ,
	t1.owner_tel_r                    ,
	t1.device_name_r                  ,
	t1.secret_level_r                 ,
	t1.unified_id_r                   ,
	t1.device_model_r                 ,
	t1.device_spec_r                  ,
	t1.manufacturer_id_r              ,
	t1.lika_date_r                    ,
	t1.original_value_r               ,
	t1.existing_performance_r         ,
	t1.reforming_reason_r             ,
	t1.after_performance_r            ,
	t1.transform_way_r                ,
	t1.budget_r                       ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   ,
	t1.net_value_r                    ,
	t1.device_category_r              ,
	t1.created_by_person_r            
from assets_reconstruction_r 	t1


			and t1.ID =#{id}


			and t1.RECONSTRUCTION_ID_R =#{reconstructionIdR}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATED_BY_DEPT_R =#{createdByDeptR}


			and t1.FORM_STATE_R =#{formStateR}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.OWNER_DEPT_R =#{ownerDeptR}


			and t1.OWNER_ID_R =#{ownerIdR}


			and t1.OWNER_TEL_R =#{ownerTelR}


			and t1.DEVICE_NAME_R =#{deviceNameR}


			and t1.SECRET_LEVEL_R =#{secretLevelR}


			and t1.UNIFIED_ID_R =#{unifiedIdR}


			and t1.DEVICE_MODEL_R =#{deviceModelR}


			and t1.DEVICE_SPEC_R =#{deviceSpecR}


			and t1.MANUFACTURER_ID_R =#{manufacturerIdR}


			and t1.LIKA_DATE_R >= #{likaDateRBegin}


			and t1.LIKA_DATE_R <= #{likaDateREnd}


			and t1.ORIGINAL_VALUE_R =#{originalValueR}


			and t1.EXISTING_PERFORMANCE_R =#{existingPerformanceR}


			and t1.REFORMING_REASON_R =#{reformingReasonR}


			and t1.AFTER_PERFORMANCE_R =#{afterPerformanceR}


			and t1.TRANSFORM_WAY_R =#{transformWayR}


			and t1.BUDGET_R =#{budgetR}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}


			and t1.NET_VALUE_R =#{netValueR}


			and t1.DEVICE_CATEGORY_R =#{deviceCategoryR}


			and t1.CREATED_BY_PERSON_R =#{createdByPersonR}
