mapper.select:
select
	t1.id                             ,
	t1.acceptance_id                  ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.furniture_name                 ,
	t1.furniture_category             ,
	t1.manufacturer_id                ,
	t1.guarantee_period               ,
	t1.guarantee_date                 ,
	t1.furniture_spec                 ,
	t1.position_id                    ,
	t1.check_num                      ,
	t1.unit_price                     ,
	t1.total_price                    ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   ,
	t1.attribute_16                   ,
	t1.attribute_17                   ,
	t1.attribute_18                   ,
	t1.attribute_19                   ,
	t1.attribute_20                   
from assets_fur_acceptance_rel 	t1


			and t1.ID =#{id}


			and t1.ACCEPTANCE_ID =#{acceptanceId}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.FURNITURE_NAME =#{furnitureName}


			and t1.FURNITURE_CATEGORY =#{furnitureCategory}


			and t1.MANUFACTURER_ID =#{manufacturerId}


			and t1.GUARANTEE_PERIOD =#{guaranteePeriod}


			and t1.GUARANTEE_DATE >= #{guaranteeDateBegin}


			and t1.GUARANTEE_DATE <= #{guaranteeDateEnd}


			and t1.FURNITURE_SPEC =#{furnitureSpec}


			and t1.POSITION_ID =#{positionId}


			and t1.CHECK_NUM =#{checkNum}


			and t1.UNIT_PRICE =#{unitPrice}


			and t1.TOTAL_PRICE =#{totalPrice}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}


			and t1.ATTRIBUTE_16 =#{attribute16}


			and t1.ATTRIBUTE_17 =#{attribute17}


			and t1.ATTRIBUTE_18 =#{attribute18}


			and t1.ATTRIBUTE_19 =#{attribute19}


			and t1.ATTRIBUTE_20 =#{attribute20}
