			and t1.TDEVICE_NAME =#{tdeviceName}


			and t1.TDEVICE_CODE =#{tdeviceCode}


			and t1.TDEVICE_MODEL =#{tdeviceModel}


			and t1.TDEVICE_NUM =#{tdeviceNum}


			and t1.BELONG_MODEL =#{belongModel}


			and t1.TEST_TYPE =#{testType}


			and t1.TEST_NATURE =#{testNature}


			and t1.TEST_CONDITION =#{testCondition}


			and t1.SUPPORT_TOOL =#{supportTool}


			and t1.ORDER_START_TIME >= #{orderStartTimeBegin}


			and t1.ORDER_START_TIME <= #{orderStartTimeEnd}


			and t1.ORDER_FINISH_TIME >= #{orderFinishTimeBegin}


			and t1.ORDER_FINISH_TIME <= #{orderFinishTimeEnd}


			and t1.LAB_DEVICE_ID =#{labDeviceId}


