			and t1.APPROVAL_START_TIME <= #{approvalStartTimeEnd}


			and t1.APPROVAL_FINISH_TIME >= #{approvalFinishTimeBegin}


			and t1.APPROVAL_FINISH_TIME <= #{approvalFinishTimeEnd}


			and t1.ACTUAL_START_TIME >= #{actualStartTimeBegin}


			and t1.ACTUAL_START_TIME <= #{actualStartTimeEnd}


			and t1.ACTUAL_FINISH_TIME >= #{actualFinishTimeBegin}


			and t1.ACTUAL_FINISH_TIME <= #{actualFinishTimeEnd}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


